package com.poovarasan;

public class angle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=3;i++)
		{
			for(int j=3;j>i;j--)
			{
				System.out.print(" ");
			}

			{
				System.out.print("*");
			}
			System.out.println();
	}

}
}