package com.poovarasan;

public class mad {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int b=5;
for(int r=1;r<=b;r++)
{
	for(int j=b;j>r;j--)
		
	{
		System.out.print(" ");
	}
	for(int c=1;c<=r;c++)
	{
		System.out.print("*");
	}
	for(int k=1;k<r;k++)
	{
		System.out.print("*");
	}
		
	System.out.println();
	
}
	}

}
