package com.ARRAY;

public class onetwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n=5;
for(int i=1;i<=n;i=i+2)
{
	for(int r=n-1;r>i;r--)
	{
		System.out.print(" ");	
	}
	
	for(int c=1;c<=i;c++)
	{
		System.out.print(c+" ");
	}System.out.println();
	}
}
	}



