package com.poovarasan;

public class slide {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1;i<=4;i++)
{
	for(int j=1;j<i;j++)
	{
		System.out.print(" ");
	}
	for(int k=i;k<=i;k++)
	{
		System.out.print("*");
	}
	System.out.println();
}
	}

}
